import { useState, useEffect } from 'react';
import './App.css';
import axios from 'axios';
function App() {
  const [persona, setPersona] = useState([]);

  useEffect(() => {
    async function obtenerPersonas() {
      try {
        const response = await axios.get('http://localhost:4000/personas');
        const data = response.data;
        setPersona(data);
      } catch (error) {
        console.error(error);
      }
    }
    obtenerPersonas();
  }, []);
  const insertatNombre = (e) => {
    const value = e.target.value;
    const regex = /^[a-zA-ZÁÉÍÓÚáéíóúñÑ\s\-\']+$/; 
    if (value === "" || regex.test(value)) {
      setPersona({
          ...persona,
          nombre: value,
      });
    }

  }

  const insertarDNI = (e) => {
    const value = e.target.value;
    const regex = /^[0-9\b]+$/; 
    //regex permite solo números y caracteres de retroceso (\b).
    if (value === "" || regex.test(value)) {
      setPersona({
          ...persona,
          dni: value,
      });
    }

  }

  const enviarInformacion = (e) => {
    console.log('enviado');
    console.log(persona);
  }

  return (
    <div className="container">
      <div className="form">
        <p class="title">Registro</p>
        <div class="form-row">
          <label class="name">
            <span>Nombre</span>
            <input class="input" type="text" value="" onChange={insertatNombre} />
          </label>
          <br />
          <label class="dni">
            <span>DNI</span>
            <input class="input" type="text" value="" onChange={insertarDNI} />
          </label>
          <br />
          <button id="btn" onClick={enviarInformacion}>Enviar informacion</button>
        </div>
        </div>
    </div>

  );
}

export default App;
